function isSundayMorning() {
    // Get the current date and time
    const currentDate = new Date();

    // Check if it's Sunday (0 corresponds to Sunday, 1 to Monday, and so on)
    const isSunday = currentDate.getDay() === 1;

    // Check if it's between 6 and 9 in the morning
    const isMorning =
        currentDate.getHours() >= 6 && currentDate.getHours() < 12;

    // Return true if it's Sunday and in the specified time range, otherwise false
    return isSunday && isMorning;
}


